// import 'react-native-gesture-handler';
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
	StyleSheet,
	Text,
	View,
	SafeAreaView,
	FlatList,
	TouchableOpacity,
	Button,
} from "react-native";

import { useScrollToTop } from "@react-navigation/native";

import { SearchBar } from "react-native-elements";

import { select_id, select_genre, fetch_rooms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";

import GlobalTitles from "../components/global-titles";
// import SearchBar from "../components/searchbar";
import GenreMenu from "../components/genre-menu";
import Location from "../components/location";
import RoomElementHome from "../components/roomElementHome";

// API Imports
import { getRooms, getGenre } from "../../apiService";
import { ScrollView } from "react-native-gesture-handler";

const TITLES = [
	"Let's find your next adventure ðŸ¤ŸðŸ¼",
	"Are you ready for the action? ðŸ•µðŸ½ ",
	"Find the best escapes in town ðŸ”ðŸ”",
];
//TODO: ADD MORE TITLES
//TODO: IMPLEMENT SEARCHBAR
//TODO: HIDE SEARCH BAR ON SCROLL
//TODO: SCROLL BACK UP WITH BUTTON (IMPLEMENTED BUT CRASHES)

const mockROOMS = [{}];
const title = TITLES[Math.round(Math.random() * (TITLES.length - 1))];

export default function Home({ navigation }) {
	const dispatch = useDispatch();
	const [genreFilter, setGenreFilter] = useState("All");
	const [rooms, setRooms] = useState([]);
	const [filteredRooms, setFilteredRooms] = useState();

	// Scroll to top on tab press
	const ref = React.useRef(null);
	useScrollToTop(ref);

	const allRooms = [{ name: "MOCK ROOM" }];

	// Get the room data and send it to redux.
	useEffect(() => {
		getRooms().then(rooms => setRooms(rooms));
		dispatch(fetch_rooms(rooms));
	}, []);

	//This useEffect is used to filter the rooms when genre is clicked.
	useEffect(() => {
		handleFilterChange();
	}, [genreFilter]);

	//Send selected room id to redux and navigate to room details screen.
	const selectRoomHandler = id => {
		dispatch(select_id(id));
		navigation.navigate("roomDetails");
	};

	// If the genre selected is different than all, filter the rooms, else display all.
	const handleFilterChange = () => {
		if (genreFilter !== "All") {
			const res = rooms.filter(room => room.genres.includes(genreFilter));
			setFilteredRooms(res);
		} else {
			setFilteredRooms();
		}
	};

	const selectGenreHandler = genre => {
		if (genre === genreFilter) {
			setGenreFilter("All");
		} else {
			setGenreFilter(genre);
		}
	};

	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<Location />
				<GlobalTitles title={title} />
				{/* <SearchBar handleChange={handleChange} /> */}
				{/* <SearchBar
					placeholder="Try typing a name..."
					lightTheme
					round
					onChangeText={search => updateSearch(search)}
					autoCorrect={false}
				/> */}
				<View style={styles.genreMenuContainer}>
					<GenreMenu handler={selectGenreHandler} genreSelected={genreFilter} />
				</View>
				<ScrollView ref={ref}>
					<FlatList
						style={{
							flex: 1,
						}}
						showsVerticalScrollIndicator={false}
						bounces={false}
						data={filteredRooms ? filteredRooms : rooms}
						renderItem={({ item, index }) => (
							<TouchableOpacity onPress={() => selectRoomHandler(item._id)}>
								<RoomElementHome room={item} key={index} />
							</TouchableOpacity>
						)}
					/>
				</ScrollView>
			</View>
			<StatusBar style="auto" />
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginRight: "3%",
		marginLeft: "3%",
		marginTop: "1%",
		height: "100%",
	},
	genreMenuContainer: {
		marginHorizontal: "3%",
		marginVertical: "3%",
	},
	pressed: { color: "#FF267C" },
	clean: { color: "#828282" },
});
