import React, { useEffect, useState } from "react";
import { StatusBar } from "expo-status-bar";
import {
	StyleSheet,
	Text,
	View,
	Image,
	SafeAreaView,
	TouchableOpacity,
	Button,
} from "react-native";

import RenderedList from "../components/RenderedList";

export default function Lists() {
	const [pressed, setPressed] = useState("saved");
	const [listToRender, setListToRender] = useState("Saved List");

	const handleSelectedStyle = () => {
		if (pressed === "saved") {
			setPressed("completed");
			setListToRender("Completed List");
		} else {
			setPressed("saved");
			setListToRender("Saved List");
		}
	};
	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<View style={styles.listSelectorContainer}>
					<View style={styles.listSelector}>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text style={pressed === "saved" ? styles.pressed : styles.clean}>
								Saved
							</Text>
						</TouchableOpacity>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text style={pressed === "saved" ? styles.clean : styles.pressed}>
								Completed
							</Text>
						</TouchableOpacity>
					</View>
				</View>

				<RenderedList list={listToRender} />
				<View style={styles.buttonContainer}>
					<Button style={styles.addNewBtn} title="+"></Button>
				</View>
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginHorizontal: "3%",
		height: 780,
		backgroundColor: "red",
	},
	listSelectorContainer: {
		marginTop: "8%",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
	},
	listSelector: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
		paddingHorizontal: 10,
	},
	selectorElement: {
		padding: 10,
		width: "25%",
		justifyContent: "center",
		alignItems: "center",
	},
	pressed: {
		color: "#5FC9F8",
	},
	clean: {
		color: "#828282",
	},
	// addNewBtn: {
	// 	position: "absolute",
	// 	right: 15,
	// 	marginTop: "90%",
	// 	zIndex: 1000,
	// },
	buttonContainer: {
		height: 40,
		width: 40,
		position: "relative",
		marginLeft: "90%",
		marginTop: "50%",
		zIndex: 1000,
	},
});
