import React, { useEffect, useState } from "react";
import { StyleSheet, Text, View, Image, SafeAreaView } from "react-native";
import { shallowEqual, useSelector } from "react-redux";

import { getOne } from "../../apiService";

export default function RoomDetails() {
	// const [selectedId, setSelectedId] = useState(null);
	const [selectedRoom, setSelectedRoom] = useState(null);

	//THIS CONSOLE LOG KEEPS RE-RENDERING
	console.log(selectedRoom);
	const selectedId = useSelector(state => state.selected_id, shallowEqual);

	useEffect(() => {
		// Call Api service and set the selected room.
		getOne(selectedId).then(room => setSelectedRoom(room));
	}, []);

	return (
		<View style={styles.container}>
			<Image source={{ uri: selectedRoom.imgURL }} style={styles.img} />
			<View style={styles.infoContainer}>
				<Text>Hey</Text>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	infoContainer: {
		position: "absolute",
		backgroundColor: "#fff",
		top: "45%",
		width: "100%",
		height: "100%",
		borderTopEndRadius: 20,
		borderTopStartRadius: 20,
	},
	marginContainer: {
		marginHorizontal: "3%",
		marginTop: "1%",
		height: "100%",
	},
	img: {
		height: "60%",
		width: "100%",
	},
});

