const initialUser = {};
function user(state = initialUser, action) {
	switch (action.type) {
		case "SET_USER_INFO":
			return action.user;
		case "ADD_SAVED_LIST":
			const newList = [...state.user.fav_list, action.roomId];
			state.user.fav_list = newList;
			return state;

		case "ADD_COMPLETED_LIST":
			if (!state.user.completed_list.includes(action.roomId)) {
				const newList = [...state.user.completed_list, action.roomId];
				state.user.completed_list = newList;
			}
			return state;

		case "DELETE_FROM_SAVED":
			const indexSav = state.user.fav_list.indexOf(action.roomId);
			state.user.fav_list.splice(indexSav, indexSav + 1);
			return state;

		case "DELETE_FROM_COMPLETED":
			const indexComp = state.user.completed_list.indexOf(action.roomId);
			state.user.completed_list.fav_list.splice(indexComp, indexComp + 1);
			return state;

		default:
			return state;
	}
}
const initialLogin = false;
function login(state = initialLogin, action) {
	switch (action.type) {
		case "SET_LOGIN_TRUE":
			return true;
		default:
			return state;
	}
}
const initialId = null;
function selected_id(state = initialId, action) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			return action.id;
		default:
			return state;
	}
}
const initialRooms = [];
function rooms(state = initialRooms, action) {
	switch (action.type) {
		case "SELECT_GENRE":
			return action.genre;
		case "FETCH_ROOMS":
			return action.list;

		default:
			return state;
	}
}

export default reducers;

