export const select_id = id => ({
	type: "SELECT_ROOM_ID",
	id,
});
export const select_genre = genre => ({
	type: "SELECT_GENRE",
	genre,
});

export const fetch_rooms = list => ({
	type: "FETCH_ROOMS",
	list,
});

export const add_saved_list = roomId => ({
	type: "ADD_SAVED_LIST",
	roomId,
});

export const add_completed_list = roomId => ({
	type: "ADD_COMPLETED_LIST",
	roomId,
});

export const delete_from_saved = roomId => ({
	type: "DELETE_FROM_SAVED",
	roomId,
});
export const delete_from_completed = roomId => ({
	type: "DELETE_FROM_COMPLETED",
	roomId,
});

export const set_login_true = () => ({
	type: "SET_LOGIN_TRUE",
});

export const set_user_info = user => ({
	type: "SET_USER_INFO",
	user,
});
