import React, { useEffect } from "react";
import {
	StyleSheet,
	FlatList,
	Text,
	View,
	TouchableOpacity,
	ScrollView,
} from "react-native";

import RoomElementList from "./roomElementList";

export default function RenderedList({ list, handleCompleted, handleDelete }) {
	useEffect(() => {}, [list]);

	const handleComp = room => {
		handleCompleted(room);
	};

	const handleDel = room => {
		handleDelete(room);
	};
	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={list}
					renderItem={({ item }) => (
						<View>
							<RoomElementList
								room={item}
								handleCompleted={() => handleComp(item)}
								handleDelete={() => handleDel(item)}
							/>
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
