import React from "react";
import { TouchableOpacity } from "react-native";
import { Image, StyleSheet, Text, View } from "react-native";
import { useDispatch } from "react-redux";

import { add_saved_list } from "../redux/actions";

export default function RoomElementHome(props) {
	const img = { uri: props.room.imgURL };

	const dispatch = useDispatch();

	const handleAddToFavorites = room => {
		dispatch(add_saved_list(room));
	};

	return (
		<View style={styles.elementContainer}>
			<View style={styles.containerText}>
				<Text style={{ fontSize: 17 }}>{props.room.name}</Text>
				<Text> {props.room.duration} </Text>
			</View>
			<View style={styles.containerImg}>
				<Text style={styles.img}>Hello</Text>
				{/* <Image source={img} style={styles.img} /> */}
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	elementContainer: {
		height: 130,
		width: 387,
		borderWidth: 0.7,
		borderColor: "#BDBDBD",
		margin: "2%",
		borderRadius: 8,
		flexDirection: "row",
	},
	containerImg: {
		backgroundColor: "lightgreen",
		width: "30%",
	},
	img: {
		width: "100%",
		height: "100%",
		borderTopRightRadius: 15,
		borderTopLeftRadius: 15,
	},
	containerText: {
		paddingHorizontal: "4%",
		paddingVertical: "2%",
		alignItems: "flex-start",
		justifyContent: "flex-start",
		backgroundColor: "blue",
		width: "70%",
	},
});
