import React, { useEffect, useState } from "react";

import {
	StyleSheet,
	Text,
	View,
	Image,
	SafeAreaView,
	ScrollView,
	Button,
	Linking,
} from "react-native";
import { shallowEqual, useSelector } from "react-redux";

import { getOne } from "../../apiService";

const mockRoom = {
	contact_info: {
		address: "Baro, 17, 08921, Santa Coloma de Gramenet",
		phone_number: "",
		email: "info@escapesantacoloma.com",
		websiteURL: "www.escapebarcelona.com",
		booking_website: "https://www.escapebarcelona.com/reservas.php",
	},
	price: ["", ""],
	genres: ["", "", ""],
	_id: 8,
	name: "",
	rating: "",
	participants: "",
	difficulty: "",
	description: "",
	duration: 90,
	imgURL: "a",
};

export default function RoomDetails() {
	const [selectedRoom, setSelectedRoom] = useState(mockRoom);
	const selectedId = useSelector(state => state.selected_id, shallowEqual);

	useEffect(() => {
		// Call Api service and set the selected room.
		getOne(selectedId).then(room => setSelectedRoom(room));
	}, []);

	const handleLinking = () => {
		Linking.openURL(selectedRoom.contact_info.booking_website);
	};
	return (
		<SafeAreaView style={styles.marginContainer}>
			<Text style={styles.roomTitle}> {selectedRoom.name} </Text>
			<Image source={{ uri: selectedRoom.imgURL }} style={styles.img} />
			<View style={styles.infoContainer}>
				<ScrollView>
					<View style={styles.descriptionContainer}>
						<Text style={styles.detailsTitle}>Description</Text>
						<Text style={styles.descriptionText}>
							{selectedRoom.description}
						</Text>
					</View>
					<Text style={styles.detailsTitle}>Details </Text>
					<View style={styles.detailsContainer}>
						<View style={styles.iconDetails}>
							<View style={styles.iconDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={styles.detailsIcon}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.rating}{" "}
								</Text>
							</View>
							<View style={styles.iconDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={styles.detailsIcon}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.difficulty}{" "}
								</Text>
							</View>
							<View style={styles.iconDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={styles.detailsIcon}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.duration}'{" "}
								</Text>
							</View>
							<View style={styles.iconDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={styles.detailsIcon}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.participants}{" "}
								</Text>
							</View>
							<View style={styles.iconDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={styles.detailsIcon}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.price[0]} - {selectedRoom.price[1]}â‚¬
								</Text>
							</View>
						</View>
						<View style={styles.contactDetails}>
							<View style={styles.contactDetailsElement}>
								<Image
									source={require("../../assets/Ghost_face.png")}
									style={{ height: 20, width: 20 }}
								/>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.contact_info.address}{" "}
								</Text>
							</View>

							<View style={styles.contactDetailsElement}>
								<Text style={styles.smallTitle}>Genre: </Text>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.genres}{" "}
								</Text>
							</View>

							<View style={styles.contactDetailsElement}>
								<Text style={styles.smallTitle}>Contact: </Text>
								<Text style={{ fontWeight: "200" }}>
									{" "}
									{selectedRoom.contact_info.phone_number}{" "}
								</Text>
							</View>
							<View style={styles.btnMargin}>
								<Button
									onPress={handleLinking}
									title="BOOK NOW"
									style={styles.bookBtn}
									color="black"
								/>
							</View>
						</View>
					</View>
				</ScrollView>
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	infoContainer: {
		position: "absolute",
		backgroundColor: "#fff",
		top: "45%",
		width: "100%",
		height: "100%",
		borderTopEndRadius: 20,
		borderTopStartRadius: 20,
		padding: "4%",
	},
	marginContainer: {
		marginHorizontal: "3%",
		marginTop: "1%",
		height: "100%",
	},
	roomTitle: {
		marginTop: 10,
		fontSize: 25,
		// fontFamily: "",
		fontWeight: "500",
	},
	img: {
		height: "60%",
		width: "100%",
		borderTopEndRadius: 20,
		borderTopStartRadius: 20,
		marginTop: 10,
	},
	detailsTitle: {
		fontSize: 25,
		fontWeight: "400",
		paddingBottom: 5,
	},
	descriptionContainer: {
		justifyContent: "center",
	},
	descriptionText: {
		fontSize: 15,
		paddingBottom: 10,
	},
	detailsContainer: {
		width: "100%",
		height: "100%",
		flexDirection: "row",
		padding: 2,
	},
	iconDetailsElement: {
		marginVertical: 5,
		flexDirection: "row",
		// justifyContent: "center",
		alignItems: "center",
	},
	detailsIcon: {
		height: 35,
		width: 35,
	},
	contactDetailsElement: {
		flexDirection: "row",
		marginVertical: 5,
		alignItems: "center",
		width: "90%",
	},
	btnMargin: {
		padding: 18,
		backgroundColor: "#62DB93",
		alignItems: "center",
		justifyContent: "center",
		width: "50%",
	},
	bookBtn: {
		// padding: 4,
	},
});

