import React, { useEffect, useState } from "react";
import { StyleSheet, FlatList, View, ScrollView } from "react-native";
import { useSelector, useDispatch } from "react-redux";

import { deleteFromFavs, postToCompleted } from "../../apiService";

import { add_completed_list, delete_from_saved } from "../redux/actions";

import RoomElementList from "./roomElementList";

export default function SavedList({ list, userId }) {
	const favRooms = [];
	const roomList = useSelector(state => state.rooms);
	if (roomList.length) {
		renderRoomsById();
	}
	let check = false;
	useEffect(() => {}, [check]);
	const dispatch = useDispatch();

	const handleCompleted = roomId => {
		dispatch(add_completed_list(roomId));
		dispatch(delete_from_saved(roomId));
		check = !check;
		deleteFromFavs(roomId, userId);
		postToCompleted(roomId, userId);
	};
	const handleDelete = roomId => {
		dispatch(delete_from_saved(roomId));
		deleteFromFavs(roomId, userId);
	};

	function renderRoomsById() {
		for (let room in roomList) {
			if (list.includes(roomList[room]._id)) favRooms.push(roomList[room]);
		}
	}

	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={favRooms}
					renderItem={({ item, index }) => (
						<View>
							<RoomElementList
								room={item}
								id={index}
								userId={userId}
								handleDelete={handleDelete}
								handleComplete={handleCompleted}
							/>
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
