import { getRooms, getGenre } from "../../apiService";

function reducers(
	state = {
		selected_id: null,
		rooms: [],
		savedList: [],
		completedList: [],
	},
	action
) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			state.selected_id = action.id;
			return state;
		case "SELECT_GENRE":
			state.selected_genre = action.genre;
			return state;
		case "FETCH_ROOMS":
			state.rooms = action.roomList;
			return state;
		case "ADD_SAVED_LIST":
			state.savedList.push(action.room);
			return state;
		case "ADD_COMPLETED_LIST":
			state.completedList.push(action.room);
			return state;
		default:
			return state;
	}
}

export default reducers;
