// import 'react-native-gesture-handler';
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
	StyleSheet,
	Text,
	View,
	SafeAreaView,
	FlatList,
	TouchableOpacity,
	Animated,
} from "react-native";

import { select_id, select_genre, fetch_rooms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";

import GlobalTitles from "../components/global-titles";
import SearchBar from "../components/searchbar";
import GenreMenu from "../components/genre-menu";
import Location from "../components/location";
import RoomElementHome from "../components/roomElementHome";

import { createStackNavigator } from "@react-navigation/stack";

// API Imports
import { getRooms, getGenre } from "../../apiService";

const Stack = createStackNavigator();

const TITLES = [
	"Let's find your next adventure ü§üüèº",
	"Are you ready for the action? üïµüèΩ ",
	"Find the best escapes in town üîùüîù",
	//TODO: ADD MORE IN THE FUTURE
	//TODO: HIDE SEARCH BAR ON SCROLL
	//TODO: NOT SCROLLING TO THE BOTTOM
];

export default function Home({ navigation }) {
	const dispatch = useDispatch();
	const title = TITLES[Math.round(Math.random() * (TITLES.length - 1))];
	const [search, setSearch] = useState();
	const [rooms, setRooms] = useState();

	useEffect(() => {
		getRooms().then(rooms => setRooms(rooms));
		dispatch(fetch_rooms(rooms));
	}, []);

	const selectRoomHandler = id => {
		dispatch(select_id(id));
		navigation.navigate("roomDetails");
	};

	const filterRoomsHandler = search => {
		setRooms(rooms.filter(room => room.name.includes(search)));
	};

	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<Location />
				<GlobalTitles title={title} />
				<SearchBar on />
				<View style={styles.genreMenuContainer}>
					<GenreMenu />
				</View>
				<View style={styles.roomList}>
					<FlatList
						style={{ height: "100%" }}
						showsVerticalScrollIndicator={false}
						bounces={false}
						data={rooms}
						renderItem={({ item }) => (
							<TouchableOpacity onPress={() => selectRoomHandler(item._id)}>
								<RoomElementHome room={item} />
							</TouchableOpacity>
						)}
					/>
				</View>
			</View>
			<StatusBar style="auto" />
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginHorizontal: "3%",
		marginTop: "1%",
		height: "100%",
	},
	genreMenuContainer: {
		marginHorizontal: "3%",
		marginVertical: "3%",
	},
	roomList: {
		flexDirection: "column",
		alignItems: "center",
		justifyContent: "center",
	},
});

