import React, { useEffect, useState } from "react";
import { StyleSheet, FlatList, View, ScrollView } from "react-native";
import { useSelector, useDispatch } from "react-redux";

import { deleteFromFavs, postToCompleted } from "../../apiService";

import { add_completed_list, delete_from_saved } from "../redux/actions";

import RoomElementList from "./roomElementList";

export default function SavedList({ favList, userId }) {
	// const [favRooms, setFavRooms] = useState([]);
	const rooms = useSelector(state => state.rooms);
	const [roomList, setRoomList] = useState([]);
	// if (roomList.length) {
	// 	renderRoomsById();
	// }
	// useEffect(() => {
	// 	console.log("Entering");
	// 	if (roomList.length) renderRoomsById();
	// }, [list]);

	const dispatch = useDispatch();

	const handleCompleted = roomId => {
		dispatch(add_completed_list(roomId));
		dispatch(delete_from_saved(roomId));
		deleteFromFavs(roomId, userId);
		postToCompleted(roomId, userId);
	};
	const handleDelete = roomId => {
		dispatch(delete_from_saved(roomId));
		deleteFromFavs(roomId, userId);
	};

	useEffect(() => {
		console.log("useEffect triggered");
		if (rooms.length) {
			setRoomList(rooms.filter(el => el._id in favList));
		}
	}, [favList, JSON.stringify(rooms)]);

	// function renderRoomsById() {
	// 	//Usar filter
	// 	const roomArr = [];
	// 	for (let room in roomList) {
	// 		if (favList.includes(roomList[room]._id)) roomArr.push(roomList[room]);
	// 	}
	// 	setFavRooms(roomArr);
	// }

	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={roomList}
					renderItem={({ item, index }) => (
						<View>
							<RoomElementList
								room={item}
								id={index}
								userId={userId}
								handleDelete={handleDelete}
								handleComplete={handleCompleted}
							/>
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
