import { getRooms, getGenre } from "../../apiService";

function reducers(
	state = {
		selected_id: null,
		rooms: [],
		savedList: [],
		completedList: [],
		login: false,
	},
	action
) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			state.selected_id = action.id;
			return state;
		case "SELECT_GENRE":
			state.selected_genre = action.genre;
			return state;
		case "FETCH_ROOMS":
			state.rooms = action.list;
			return state;
		case "ADD_SAVED_LIST":
			if (state.savedList && state.savedList.length) {
				if (!state.savedList.includes(action.room))
					state.savedList.push(action.room);
			} else state.savedList.push(action.room);
			return state;
		case "ADD_COMPLETED_LIST":
			if (state.completedList) {
				if (!state.completedList.includes(action.room)) {
					state.completedList.push(action.room);
				}
			}
			return state;
		case "DELETE_FROM_SAVED":
			if (state.savedList && state.savedList.length > 1) {
				const index = state.savedList.indexOf(action.room);
				state.savedList.splice(index, index + 1);
			} else if (state.savedList && state.savedList.length === 1) {
				state.savedList = [];
			}
			return state;
		case "SET_LOGIN_TRUE":
			state.login = true;
			return state;
		default:
			return state;
	}
}

export default reducers;
