import { combineReducers } from "redux";

const initialUser = {};
function user(state = initialUser, action) {
	switch (action.type) {
		case "SET_USER_INFO":
			return action.user;
		case "ADD_SAVED_LIST":
			const newList = {
				...state,
				fav_list: [...state.fav_list, action.roomId],
			};
			return newList;

		case "ADD_COMPLETED_LIST":
			const newList1 = {
				...state,
				completed_list: [...state.completed_list, action.roomId],
			};
			return newList1;

		case "DELETE_FROM_SAVED":
			// const indexSav = state.fav_list.indexOf(action.roomId);
			const filtered = state.fav_list.filter(id => id !== action.roomId);
			// const spliced = state.fav_list.splice(indexSav, indexSav + 1);
			const newState = { ...state, fav_list: [...filtered] };
			return newState;

		case "DELETE_FROM_COMPLETED":
			const indexComp = state.completed_list.indexOf(action.roomId);
			const splicedComp = state.completed_list_list.splice(
				indexComp,
				indexComp + 1
			);
			const newState1 = { ...state, completed_list_list: [...splicedComp] };
			return newState1;

		default:
			return state;
	}
}
const initialLogin = false;
function login(state = initialLogin, action) {
	switch (action.type) {
		case "SET_LOGIN_TRUE":
			return true;
		case "WRONG_CREDENTIALS":
			return "error";
		default:
			return state;
	}
}
const initialId = null;
function selected_id(state = initialId, action) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			return action.id;
		default:
			return state;
	}
}
const initialRooms = [];
function rooms(state = initialRooms, action) {
	switch (action.type) {
		case "SELECT_GENRE":
			return action.genre;
		case "FETCH_ROOMS":
			return action.list;

		default:
			return state;
	}
}

export default combineReducers({ user, login, selected_id, rooms });
