import React from "react";
import {
	StyleSheet,
	TextInput,
	Text,
	View,
	TouchableOpacity,
	Image,
} from "react-native";

export default function RenderedList({ handler, activeButton }) {
	return (
		<View style={styles.container}>
			<View style={styles.listSelectorContainer}>
				<View style={styles.listSelector}>
					<TouchableOpacity style={styles.selectorElement} onPress={handler}>
						<Text
							style={activeButton === "saved" ? styles.pressed : styles.clean}
						>
							Saved
						</Text>
					</TouchableOpacity>
					<TouchableOpacity style={styles.selectorElement} onPress={handler}>
						<Text
							style={
								activeButton === "completed" ? styles.pressed : styles.clean
							}
						>
							Completed
						</Text>
					</TouchableOpacity>
				</View>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginHorizontal: "3%",
	},
	listSelectorContainer: {
		marginTop: "8%",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
	},
	listSelector: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
		paddingHorizontal: 10,
	},
	selectorElement: {
		padding: 10,
		width: "25%",
		justifyContent: "center",
		alignItems: "center",
	},
	pressed: {
		color: "#5FC9F8",
	},
	clean: {
		color: "#828282",
	},
});
