import { getRooms, getGenre } from "../../apiService";

function reducers(
	state = {
		selected_id: null,
		rooms: [],
		login: false,
		user: {},
	},
	action
) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			state.selected_id = action.id;
			return state;
		case "SELECT_GENRE":
			state.selected_genre = action.genre;
			return state;
		case "FETCH_ROOMS":
			state.rooms = action.list;
			return state;

		case "ADD_SAVED_LIST":
			if (!state.user.fav_list.includes(action.roomId)) {
				const newList = [...state.user.fav_list, action.roomId];
				const newState = { ...user, fav_list: newList };
				state.user = newState;
			}
			return state;

		case "ADD_COMPLETED_LIST":
			if (!state.user.completed_list.includes(action.roomId)) {
				state.user.completed_list.push(action.roomId);
			}

			return state;

		case "DELETE_FROM_SAVED":
			const indexSav = state.user.fav_list.indexOf(action.roomId);
			state.user.fav_list.splice(indexSav, indexSav + 1);
			return state;

		case "DELETE_FROM_COMPLETED":
			const indexComp = state.user.completed_list.indexOf(action.roomId);
			state.user.completed_list.fav_list.splice(indexComp, indexComp + 1);
			return state;
		case "SET_LOGIN_TRUE":
			state.login = true;
			return state;

		case "SET_USER_INFO":
			state.user = action.user;
			return state;

		default:
			return state;
	}
}

export default reducers;

