import React, { useEffect } from "react";
import { StatusBar } from "expo-status-bar";
import {
	StyleSheet,
	Text,
	View,
	Image,
	SafeAreaView,
	TouchableOpacity,
} from "react-native";

import RenderedList from "../components/RenderedList";
// import ListSelector from "../components/listSelector";

//TODO: Change colors when clicking "saved" or "completed"

export default function Lists() {
	let pressed = "saved";
	let listToRender = "Saved List";

	useEffect(() => {}, [listToRender]);

	const handleSelectedStyle = () => {
		if (pressed === "saved") {
			pressed = "completed";
			listToRender = "Completed List";
		} else {
			pressed = "saved";
			listToRender = "Saved List";
		}
	};
	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<View style={styles.listSelectorContainer}>
					<View style={styles.listSelector}>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text style={pressed === "saved" ? styles.pressed : styles.clean}>
								Saved
							</Text>
						</TouchableOpacity>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text style={styles.clean}>Completed</Text>
						</TouchableOpacity>
					</View>
				</View>

				<RenderedList list={listToRender} />
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginHorizontal: "3%",
	},
	listSelectorContainer: {
		marginTop: "8%",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
	},
	listSelector: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
		paddingHorizontal: 10,
	},
	selectorElement: {
		padding: 10,
		width: "25%",
		justifyContent: "center",
		alignItems: "center",
	},
	pressed: {
		color: "#5FC9F8",
	},
	clean: {
		color: "#828282",
	},
});
