import React, { useEffect, useState } from "react";
import {
	StyleSheet,
	FlatList,
	Text,
	View,
	TouchableOpacity,
	ScrollView,
} from "react-native";
import { useSelector } from "react-redux";

import { postToFavs, getRooms } from "../../apiService";

import RoomElementList from "./roomElementList";

export default function SavedList({ list, userId }) {
	const favRooms = [];
	const roomList = useSelector(state => state.rooms);
	if (roomList.length) {
		renderRoomsById();
	}
	const handleCompleted = () => {
		console.log("Handle Completed called");
		dispatch(add_completed_list(props.room));
		dispatch(delete_from_saved(props.room));
		deleteFromFavs(props.room._id, props.userId);
		postToCompleted(props.room._id, props.userId);
	};
	const handleDelete = () => {
		console.log("Handle delete called");
		dispatch(delete_from_saved(props.room));
		deleteFromFavs(props.room._id, props.userId);
	};

	function renderRoomsById() {
		for (let room in roomList) {
			if (list.includes(roomList[room]._id)) favRooms.push(roomList[room]);
		}
	}

	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={favRooms}
					renderItem={({ item, index }) => (
						<View>
							<RoomElementList
								room={item}
								id={index}
								userId={userId}
								handleDelete={handleDelete}
								handleComplete={handleCompleted}
							/>
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
