import React from "react";
import { Image, StyleSheet, Text, View } from "react-native";

export default function RoomElementHome(props) {
	const img = { uri: props.room.imgURL };
	return (
		<View style={styles.elementContainer}>
			<View style={styles.containerImg}>
				<Image source={img} style={styles.img} />
			</View>
			<View style={styles.containerText}>
				<Text style={{ fontSize: 17 }}>{props.room.name}</Text>
				<Text numberOfLines={3} style={styles.roomDescription}>
					{props.room.description}
				</Text>
			</View>
			<View style={styles.detailsContainer}>
				<View style={styles.detailsContainerElement}>
					<Image
						source={("uri: ", props.room.imgURL)}
						style={styles.detailsImg}
					/>
					<Text style={styles.detailsElementText}> {props.room.rating} </Text>
				</View>
				<View style={styles.detailsContainerElement}>
					<Image
						source={require("../../assets/Ghost_face.png")}
						style={styles.detailsImg}
					/>
					<Text style={styles.detailsElementText}>
						{" "}
						{props.room.difficulty}{" "}
					</Text>
				</View>
				<View style={styles.detailsContainerElement}>
					<Image
						source={require("../../assets/Ghost_face.png")}
						style={styles.detailsImg}
					/>
					<Text style={styles.detailsElementText}>
						{" "}
						{props.room.duration}'{" "}
					</Text>
				</View>
				<View style={styles.detailsContainerElement}>
					<Image
						source={require("../../assets/Ghost_face.png")}
						style={styles.detailsImg}
					/>
					<Text style={styles.detailsElementText}>
						{" "}
						{props.room.participants}{" "}
					</Text>
				</View>
				<View style={styles.detailsContainerElement}>
					<Image
						source={require("../../assets/Ghost_face.png")}
						style={styles.detailsImg}
					/>
					<Text style={styles.detailsElementText}>
						{" "}
						{props.room.price[0]} - {props.room.price[1]}{" "}
					</Text>
				</View>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	elementContainer: {
		height: 300,
		width: 380,
		borderWidth: 0.7,
		borderColor: "#BDBDBD",
		margin: "2%",
		borderRadius: 25,
	},
	container: {
		paddingTop: "2%",
	},
	title: {
		width: "60%",
		fontSize: 25,
		fontWeight: "400",
	},
	img: {
		width: 100,
		height: "60%",
	},
	containerImg: {
		alignItems: "center",
		backgroundColor: "lightblue",
		borderTopLeftRadius: 25,
		borderTopRightRadius: 25,
	},
	containerText: {
		paddingHorizontal: "4%",
		paddingVertical: "2%",
	},
	roomDescription: {
		flexShrink: 10,
		paddingTop: "1%",
		color: "#828282",
	},
	detailsContainer: {
		alignItems: "center",
		justifyContent: "center",
		flexDirection: "row",
	},
	detailsContainerElement: {
		alignItems: "center",
		justifyContent: "center",
		flexDirection: "row",
		paddingHorizontal: "1%",
	},

	detailsImg: {
		width: 23,
		height: 23,
	},
	detailsElementText: {
		fontSize: 13,
	},
});
