import React, { useEffect, useState } from "react";
import {
	StyleSheet,
	FlatList,
	Text,
	View,
	TouchableOpacity,
	ScrollView,
} from "react-native";

import { postToFavs, getRooms } from "../../apiService";

import RoomElementList from "./roomElementList";

export default function SavedList() {
	const [idList, setIdList] = useState([]);
	const [roomList, setRoomList] = useState([]);

	useEffect(() => {
		postToFavs()
			.then(data => setIdList(data))
			.then(
				console.log(idList)
				// getRooms().then(rooms => {
				// 	setRoomList(rooms.map(room => idList.includes(room.id)));
				// })
			);
	}, [idList]);

	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={roomList}
					renderItem={({ item, index }) => (
						<View>
							<RoomElementList room={item} id={index} />
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
