import React from "react";
import { StatusBar } from "expo-status-bar";
import {
	StyleSheet,
	Text,
	View,
	Image,
	SafeAreaView,
	TouchableOpacity,
} from "react-native";

import GlobalTitles from "../components/global-titles";
import SearchBar from "../components/searchbar";
import GenreMenu from "../components/genre-menu";

export default function Lists() {
	let pressed = "saved";

	const handleSelectedStyle = () => {
		console.log('"Entering handle"');
		console.log("Current pressed:", pressed);
		if (pressed === "saved") pressed = "completed";
		else pressed = "saved";
		console.log("Later pressed:", pressed);
	};
	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<View style={styles.listSelectorContainer}>
					<View style={styles.listSelector}>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text style={pressed === "saved" ? styles.pressed : styles.clean}>
								Saved
							</Text>
						</TouchableOpacity>
						<TouchableOpacity
							style={styles.selectorElement}
							onPress={handleSelectedStyle}
						>
							<Text
								style={pressed === "completed" ? styles.pressed : styles.clean}
							>
								Completed
							</Text>
						</TouchableOpacity>
					</View>
				</View>
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginHorizontal: "3%",
	},
	listSelectorContainer: {
		marginTop: "8%",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
	},
	listSelector: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
		paddingHorizontal: 10,
	},
	selectorElement: {
		padding: 10,
		width: "25%",
		justifyContent: "center",
		alignItems: "center",
	},
	pressed: {
		color: "#5FC9F8",
	},
	clean: {
		color: "#828282",
	},
});
