import React from "react";
import { StyleSheet, Text, View, Button } from "react-native";
import Lists from "../screens/lists";
export default function UserSquadElement({ squad, leaveSquadHandler }) {
	return (
		<View style={styles.container}>
			<Text style={styles.title}>{squad.name}</Text>
			<Text style={styles.id}>ID: {squad.id}</Text>
			<Text style={{ marginTop: 10, color: "#FF267C" }}>Description:</Text>
			<Text style={styles.description}>"{squad.description}"</Text>
			<Text style={{ marginTop: 10, color: "#FF267C" }}>Participants:</Text>
			<View style={styles.participant}>
				<Text> {squad.users} </Text>
			</View>
			<Lists />
			<View style={{ marginBottom: 10 }}>
				<Button
					title="Leave Squad"
					color="#FC3D39"
					onPress={leaveSquadHandler}
				/>
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		paddingTop: "5%",
		height: "100%",
	},
	title: {
		width: "90%",
		fontSize: 35,
		fontWeight: "500",
	},
	id: {
		width: "90%",
		marginTop: 5,
		fontSize: 30,
		fontWeight: "400",
	},
	description: {
		width: "90%",
		marginTop: 2,
		fontSize: 20,
		fontWeight: "300",
	},
});
