// import 'react-native-gesture-handler';
import { StatusBar } from "expo-status-bar";
import React, { useEffect, useState } from "react";
import {
	StyleSheet,
	Text,
	View,
	SafeAreaView,
	FlatList,
	TouchableOpacity,
	Button,
} from "react-native";

import { SearchBar } from "react-native-elements";

import { select_id, select_genre, fetch_rooms } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";

import GlobalTitles from "../components/global-titles";
// import SearchBar from "../components/searchbar";
import GenreMenu from "../components/genre-menu";
import Location from "../components/location";
import RoomElementHome from "../components/roomElementHome";

import { createStackNavigator } from "@react-navigation/stack";

// API Imports
import { getRooms, getGenre } from "../../apiService";
import { ScrollView } from "react-native-gesture-handler";

const Stack = createStackNavigator();

const TITLES = [
	"Let's find your next adventure ðŸ¤ŸðŸ¼",
	"Are you ready for the action? ðŸ•µðŸ½ ",
	"Find the best escapes in town ðŸ”ðŸ”",
];
//TODO: ADD MORE TITLES
//TODO: IMPLEMENT SEARCHBAR
//TODO: HIDE SEARCH BAR ON SCROLL
//TODO: SCROLL BACK UP WITH BUTTON (IMPLEMENTED BUT CRASHES)
//TODO: TITLE RE-RENDERING ON GENRE CHANGE

const mockROOMS = [{}];

export default function Home({ navigation }) {
	const dispatch = useDispatch();
	const title = TITLES[Math.round(Math.random() * (TITLES.length - 1))];
	const [genreFilter, setGenreFilter] = useState("All");
	const [rooms, setRooms] = useState([]);
	const [filteredRooms, setFilteredRooms] = useState();

	const allRooms = [{ name: "MOCK ROOM" }];

	useEffect(() => {
		getRooms().then(rooms => setRooms(rooms));
		dispatch(fetch_rooms(rooms));
	}, []);

	useEffect(() => {
		console.log("Current genre filter: ", genreFilter);
		handleFilterChange();
	}, [genreFilter]);

	const selectRoomHandler = id => {
		dispatch(select_id(id));
		navigation.navigate("roomDetails");
	};

	const handleFilterChange = () => {
		if (genreFilter !== "All") {
			rooms.forEach(room => console.log(room.genres));
			const res = rooms.forEach(room => room.genres.includes(genreFilter));
			console.log(res);
			// setFilteredRooms(rooms.filter(room => room.genres.includes(genre)));
		} else {
			console.log("All selected");
			setFilteredRooms();
		}
	};

	const selectGenreHandler = genre => {
		if (genre === genreFilter) {
			setGenreFilter("All");
		} else {
			setGenreFilter(genre);
		}
	};

	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<Location />
				<GlobalTitles title={title} />
				{/* <SearchBar handleChange={handleChange} /> */}
				{/* <SearchBar
					placeholder="Try typing a name..."
					lightTheme
					round
					onChangeText={search => updateSearch(search)}
					autoCorrect={false}
				/> */}
				<View style={styles.genreMenuContainer}>
					<GenreMenu handler={selectGenreHandler} genreSelected={genreFilter} />
				</View>
				<ScrollView
				// ref={c => {
				// 	this.scroll = c;
				// }}
				>
					<FlatList
						style={{
							// height: "100%",
							// width: "100%",
							flex: 1,
						}}
						showsVerticalScrollIndicator={false}
						bounces={false}
						data={filteredRooms ? filteredRooms : rooms}
						renderItem={({ item }) => (
							<TouchableOpacity onPress={() => selectRoomHandler(item._id)}>
								<RoomElementHome room={item} />
							</TouchableOpacity>
						)}
					/>
					<View style={{ marginVertical: 10 }}>
						<Button
							title="Back to top"
							// onPress={() =>
							// 	this.scroll.scrollTo({ x: 0, y: 0, animated: true })
							// }
						/>
					</View>
				</ScrollView>
			</View>
			<StatusBar style="auto" />
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
	},
	marginContainer: {
		marginRight: "3%",
		marginLeft: "3%",
		marginTop: "1%",
		height: "100%",
	},
	genreMenuContainer: {
		marginHorizontal: "3%",
		marginVertical: "3%",
	},
	pressed: { color: "#FF267C" },
	clean: { color: "#828282" },
});
