import React, { useState } from "react";
import { StyleSheet, Text, View, Image, TextInput } from "react-native";
import { TouchableOpacity } from "react-native-gesture-handler";
import { SafeAreaView } from "react-navigation";

export default function LogIn({ navigation }) {
	const [email, setEmail] = useState("");
	const [pass, setPass] = useState("");

	const checkCredentials = () => {
		console.log("Email:", email);
		console.log("Pass:", pass);
	};

	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<View style={{ width: "100%", backgroundColor: "blue", marginTop: 50 }}>
					<Image
						source={require("../../../assets/Ghost_face.png")}
						style={styles.closeIcon}
					/>
				</View>
				<View style={styles.insideContainer}>
					<Image
						source={require("../../../assets/logo-no-bck.png")}
						style={styles.logoImg}
					/>
					<View style={{ marginTop: -40 }}>
						<View>
							<TextInput
								value={email}
								onChangeText={text => setEmail(text)}
								autoCapitalize="none"
								style={styles.input}
								placeholder="Enter valid email"
							></TextInput>
						</View>
						<TextInput
							value={pass}
							onChangeText={text => setPass(text)}
							autoCapitalize="none"
							style={styles.input}
							placeholder="Enter valid password"
							secureTextEntry={true}
						></TextInput>
						<TouchableOpacity
							style={styles.loginBtn}
							onPress={checkCredentials}
						>
							<Text style={{ color: "white" }}>Log In</Text>
						</TouchableOpacity>
						<TouchableOpacity
							style={styles.signUpBtn}
							onPress={() => navigation.navigate("signUp")}
						>
							<Text>Sign Up</Text>
						</TouchableOpacity>
					</View>
				</View>
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		justifyContent: "center",
	},
	marginContainer: {
		marginHorizontal: "10%",
		marginTop: "30%",
		marginBottom: "30%",
		height: "100%",
		alignItems: "center",
		color: "white",
		alignItems: "center",
	},
	closeIcon: {
		height: 30,
		width: 30,
		backgroundColor: "red",
	},
	insideContainer: {
		padding: 20,
		height: "100%",
		width: "95%",
		alignItems: "center",
		marginTop: 40,
	},
	logoImg: {
		marginTop: -80,
		height: 200,
		width: 200,
		marginBottom: 30,
		backgroundColor: "red",
	},
	input: {
		width: 250,
		height: 44,
		borderWidth: 1,
		borderColor: "#828282",
		borderRadius: 3,
		marginTop: 8,
		padding: 10,
	},
	loginBtn: {
		marginTop: 35,
		alignSelf: "center",
		width: "80%",
		justifyContent: "center",
		alignItems: "center",
		paddingVertical: 15,
		backgroundColor: "#FF267C",
	},
	signUpBtn: {
		marginTop: 10,
		alignSelf: "center",
		width: "70%",
		justifyContent: "center",
		alignItems: "center",
		paddingVertical: 15,
	},
});
