import React, { useEffect, useState } from "react";
import { StatusBar } from "expo-status-bar";
import {
	StyleSheet,
	Text,
	View,
	Image,
	SafeAreaView,
	TouchableOpacity,
	Button,
} from "react-native";

import RenderedList from "../components/RenderedList";

export default function Lists() {
	const [pressed, setPressed] = useState("saved");
	const [listToRender, setListToRender] = useState("Saved List");

	const handleSelectedStyle = () => {
		if (pressed === "saved") {
			setPressed("completed");
			setListToRender("Completed List");
		} else {
			setPressed("saved");
			setListToRender("Saved List");
		}
	};
	return (
		<SafeAreaView style={styles.container}>
			<View style={styles.marginContainer}>
				<View style={styles.topElements}>
					<View style={styles.listSelectorContainer}>
						<TouchableOpacity style={styles.buttonContainer}>
							<Text style={styles.addNewBtn}>+</Text>
						</TouchableOpacity>
						<View style={styles.listSelector}>
							<TouchableOpacity
								style={styles.selectorElement}
								onPress={handleSelectedStyle}
							>
								<Text
									style={pressed === "saved" ? styles.pressed : styles.clean}
								>
									Saved
								</Text>
							</TouchableOpacity>
							<TouchableOpacity
								style={styles.selectorElement}
								onPress={handleSelectedStyle}
							>
								<Text
									style={pressed === "saved" ? styles.clean : styles.pressed}
								>
									Completed
								</Text>
							</TouchableOpacity>
						</View>
					</View>
				</View>
				<RenderedList list={listToRender} />
			</View>
		</SafeAreaView>
	);
}

const styles = StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: "#fff",
		height: 780,
	},
	marginContainer: {
		marginHorizontal: "3%",
		height: 780,
	},
	listSelectorContainer: {
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
	},
	listSelector: {
		flexDirection: "row",
		justifyContent: "center",
		alignItems: "center",
		borderRadius: 20,
		paddingHorizontal: 10,
	},
	topElements: {
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
	},
	selectorElement: {
		padding: 10,
		width: "70%",
		justifyContent: "center",
		alignItems: "center",
	},
	addNewBtn: {
		fontSize: 30,
		color: "#147EFB",
	},
	buttonContainer: {
		height: 40,
		width: 40,
		alignItems: "center",
		justifyContent: "center",
		alignSelf: "flex-end",
		zIndex: 1000,
	},
});
