import React, { useEffect, useState } from "react";
import { StyleSheet, FlatList, View, ScrollView } from "react-native";
import { useSelector, useDispatch } from "react-redux";

import { deleteFromFavs, postToCompleted } from "../../apiService";

import { add_completed_list, delete_from_saved } from "../redux/actions";

import RoomElementList from "./roomElementList";

export default function SavedList({ userId }) {
	const rooms = useSelector(state => state.rooms);
	const [roomList, setRoomList] = useState([]);
	const [favList, setFavList] = useState(
		useSelector(state => state.user.fav_list)
	);

	const dispatch = useDispatch();

	useEffect(() => {
		if (rooms.length) {
			setRoomList(rooms.filter(el => favList.includes(el._id)));
		}
		console.log(favList);
	}, [JSON.stringify(favList), JSON.stringify(roomList)]);

	const handleCompleted = roomId => {
		//Update redux state
		dispatch(add_completed_list(roomId));
		dispatch(delete_from_saved(roomId));
		//Update DB
		deleteFromFavs(roomId, userId);
		postToCompleted(roomId, userId);
	};
	const handleDelete = roomId => {
		dispatch(delete_from_saved(roomId));
		deleteFromFavs(roomId, userId);
	};

	return (
		<View style={styles.container}>
			<ScrollView>
				<FlatList
					style={{
						flex: 1,
					}}
					showsVerticalScrollIndicator={false}
					bounces={false}
					data={roomList}
					renderItem={({ item, index }) => (
						<View>
							<RoomElementList
								room={item}
								id={index}
								userId={userId}
								handleDelete={handleDelete}
								handleComplete={handleCompleted}
							/>
						</View>
					)}
				/>
			</ScrollView>
		</View>
	);
}

const styles = StyleSheet.create({
	container: {
		width: "100%",
		flexDirection: "row",
		alignItems: "center",
		justifyContent: "center",
		marginVertical: 10,
	},
	input: {
		height: 35,
		width: "80%",
		borderWidth: 1,
		borderRadius: 15,
		borderColor: "#AAAAAA",
		paddingLeft: 7,
	},
	filterContainer: {
		width: "20%",
		justifyContent: "center",
		alignItems: "center",
	},
});
