import { getRooms, getGenre } from "../../apiService";

function reducers(
	state = { selected_id: null, selected_genre: null, rooms: null },
	action
) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			state.selected_id = action.id;
			return state;
		case "SELECT_GENRE":
			state.selected_genre = action.genre;
			return state;
		case "FETCH_ROOMS":
			getRooms().then(rooms => (state.rooms = rooms));
			return state;
		default:
			return state;
	}
}

export default reducers;
