import React from "react";
import { Linking } from "react-native";
import { Button } from "react-native";
import { Image, StyleSheet, Text, View } from "react-native";
import { useDispatch } from "react-redux";

import { add_saved_list } from "../redux/actions";

export default function RoomElementHome(props) {
	const img = { uri: props.room.imgURL };

	const dispatch = useDispatch();

	const handleBookNow = () => {
		Linking.openURL(props.room.contact_info.booking_website);
	};
	const handleAddToFavorites = room => {
		dispatch(add_saved_list(room));
	};

	return (
		<View style={styles.elementContainer}>
			<View style={styles.containerText}>
				<Text style={styles.title}>{props.room.name}</Text>
				<View style={{ flexDirection: "row" }}>
					<Text style={styles.smallTextTitle}>Price: </Text>
					<Text style={styles.smallText}>
						{props.room.price[0]} - {props.room.price[1]} â‚¬
					</Text>
				</View>
				<View style={{ flexDirection: "row" }}>
					<Text style={styles.smallTextTitle}>Genre: </Text>
					<Text style={styles.smallText}>{props.room.genres[0]}</Text>
				</View>
				<View style={styles.buttonContainer}>
					<Button title="Book Now" onPress={handleBookNow} />
				</View>
			</View>
			<View style={styles.containerImg}>
				<Image source={img} style={styles.img} />
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	elementContainer: {
		height: 130,
		width: 387,
		borderWidth: 0.7,
		borderColor: "#BDBDBD",
		margin: "2%",
		borderRadius: 8,
		flexDirection: "row",
	},
	containerImg: {
		width: "30%",
	},
	img: {
		width: "100%",
		height: "100%",
		borderTopRightRadius: 8,
		borderBottomRightRadius: 8,
	},
	containerText: {
		paddingHorizontal: "4%",
		paddingVertical: "2%",
		alignItems: "flex-start",
		justifyContent: "flex-start",
		width: "70%",
	},
	title: {
		fontSize: 19,
		paddingBottom: 4,
		fontWeight: "300",
	},
	smallText: {
		fontSize: 14,
		paddingBottom: 1,
		fontWeight: "200",
	},
	smallTextTitle: {
		fontStyle: "italic",
		fontWeight: "300",
	},
	buttonContainer: {
		alignItems: "center",
		justifyContent: "center",
		width: "100%",
		height: "50%",
		fontSize: 20,
	},
});
