import React from "react";
import { Linking } from "react-native";
import { TouchableHighlight } from "react-native";
import { TouchableOpacity } from "react-native";
import { Button } from "react-native";
import { Image, StyleSheet, Text, View } from "react-native";
import { useDispatch, useSelector } from "react-redux";

import { add_completed_list, delete_from_saved } from "../redux/actions";

export default function RoomElementHome(props) {
	const img = { uri: props.room.imgURL };

	const dispatch = useDispatch();

	const handleCompleted = room => {
		dispatch(add_completed_list(room));
		dispatch(delete_from_saved(room));
	};
	const handleDelete = room => {
		dispatch(delete_from_saved(room));
		props.handler();
	};

	const handleBookNow = () => {
		Linking.openURL(props.room.contact_info.booking_website);
	};

	return (
		<View style={styles.elementContainer}>
			<View style={styles.containerText}>
				<Text style={styles.title} numberOfLines={1}>
					{props.room.name}
				</Text>

				<View style={styles.buttonContainer}>
					<Button title="Book Now" onPress={handleBookNow} />
				</View>

				<View style={styles.completedDeleteContainer}>
					<TouchableOpacity onPress={() => handleCompleted(props.room)}>
						<Image
							source={require("../../assets/Ghost_face.png")}
							style={styles.completedIcon}
						/>
					</TouchableOpacity>
					<TouchableOpacity onPress={() => handleDelete(props.room)}>
						<Image
							source={require("../../assets/Ghost_face.png")}
							style={styles.deleteIcon}
						/>
					</TouchableOpacity>
				</View>
			</View>
			<View style={styles.containerImg}>
				<Image source={img} style={styles.img} />
			</View>
		</View>
	);
}

const styles = StyleSheet.create({
	elementContainer: {
		height: 130,
		width: 387,
		borderWidth: 0.7,
		borderColor: "#BDBDBD",
		margin: "2%",
		borderRadius: 8,
		flexDirection: "row",
	},
	containerImg: {
		width: "30%",
	},
	img: {
		width: "100%",
		height: "100%",
		borderTopRightRadius: 8,
		borderBottomRightRadius: 8,
	},
	containerText: {
		paddingHorizontal: "4%",
		paddingVertical: "2%",
		alignItems: "flex-start",
		justifyContent: "flex-start",
		width: "70%",
	},
	title: {
		fontSize: 19,
		paddingBottom: 4,
		fontWeight: "300",
	},
	smallText: {
		fontSize: 14,
		paddingBottom: 1,
		fontWeight: "200",
	},
	buttonContainer: {
		alignItems: "center",
		justifyContent: "center",
		width: "100%",
		height: 40,
		marginTop: 5,
		fontSize: 20,
	},
	completedDeleteContainer: {
		alignItems: "center",
		justifyContent: "center",
		width: "100%",
		height: 40,
		flexDirection: "row",
	},
	completedIcon: {
		height: 30,
		width: 30,
		color: "green",
	},
	deleteIcon: {
		height: 30,
		width: 30,
		color: "red",
	},
});
