import { getRooms, getGenre } from "../../apiService";

function reducers(
	state = {
		selected_id: null,
		rooms: [],
		login: false,
		user: {},
	},
	action
) {
	switch (action.type) {
		case "SELECT_ROOM_ID":
			state.selected_id = action.id;
			return state;
		case "SELECT_GENRE":
			state.selected_genre = action.genre;
			return state;
		case "FETCH_ROOMS":
			state.rooms = action.list;
			return state;

		case "ADD_SAVED_LIST":
			if (!state.user.fav_list.includes(action.roomId))
				state.user.fav_list.push(action.roomId);
			else state.user.fav_list.push(action.roomId);
			return state;

		case "ADD_COMPLETED_LIST":
			if (!state.user.completed_list.includes(action.roomId)) {
				state.user.completed_list.push(action.roomId);
			}

			return state;

		case "DELETE_FROM_SAVED":
			const index = state.user.fav_list.indexOf(action.roomId);
			state.user.fav_list.splice(index, index + 1);

			return state;
		case "DELETE_FROM_COMPLETED":
			if (
				state.user.completed_list.fav_list &&
				state.user.completed_list.length > 1
			) {
				const index = state.user.completed_list.indexOf(action.room);
				state.user.completed_list.fav_list.splice(index, index + 1);
			} else {
				state.user.completed_list = [];
			}
			return state;
		case "SET_LOGIN_TRUE":
			state.login = true;
			return state;

		case "SET_USER_INFO":
			state.user = action.user;
			return state;

		default:
			return state;
	}
}

export default reducers;
